sqlReaderQuery.value:
@concat(
  'SELECT * FROM ',
  pipeline().parameters.source_schema, '.',
  pipeline().parameters.source_object,
  if(empty(activity('Fetch Logs').output.runOutput),
    '',
    concat(
      ' WHERE ',
      pipeline().parameters.watermark_column,
      ' > ''',
      activity('Fetch Logs').output.runOutput,
      ''''
    )
  )
)

watermark_value.value:
@coalesce(
  activity('Catch New Watermark Value').output.runOutput,
  activity('Fetch Logs').output.runOutput
)
